const initialState = {
	productCategories: [],
	allProducts: [],
	productDetail: [],
	page: 0,
	setPagination: {
		filter: "",
		valueFilter: "",
		valueFilter2: "",
	},
	favoriteProducts: [],
};

const GET = {}

const rootReducer = (state = initialState, action) => {
	switch (action.type) {
        case GET: {
            return {
                ...state,
                allProducts: action.payload
            }
        }
		default: {
			return state;
		}
	}
};

export default rootReducer;
